BorderLabel labelYAxis = cht.Grid.BorderLabelList.GetBorderLabel("Y Axis");
if (null != labelYAxis)
{
    labelYAxis.RelativeLocation = RelativeLocation.RightMiddle;
    labelYAxis.FlipText = true;
}


